create table questions
(
    id               bigserial not null
        constraint questions_pkey
            primary key,
    conditions       integer,
    correlative      integer   not null,
    display          boolean,
    id_father        integer,
    image            varchar(255),
    name             varchar(255),
    required         boolean,
    title            varchar(5000),
    id_question_type bigint    not null
        constraint fkgtlkgdgx66mjappaf23li3f9o
            references question_type
            on delete cascade,
    id_questionnaire bigint    not null
        constraint fk6lr5qgf8nw2tpnv25f6ys58st
            references questionnaire_type
            on delete cascade,
    id_sections      bigint    not null
        constraint fkpe0hhdiijj00s97m9ac2imlda
            references sections
            on delete cascade
);

alter table questions
    owner to postgres;

